/*global tinymce:true */

tinymce.PluginManager.add('addpager', function(editor) {

	editor.addButton('addpager', {
		icon: 'code',
		tooltip: '插入分页符',
		onclick: function() {
			editor.insertContent('<p>--------------------------------分页符--------------------------------</p>');
		}
	});
});